import React, { useEffect, useState } from "react";
import Loader from "react-loaders";
import { Link } from 'react-router-dom';
import "./index.scss";
import AnimatedLetters from "../AnimatedLetters";
import nationData from "../../data/nations.json";
import playerData from "../../data/player.json"; // Assuming you have this file with players' name and uri

const Nations = () => {
    const [letterClass, setLetterClass] = useState('text-animate');
    const [searchQuery, setSearchQuery] = useState('');
    const [filteredNations, setFilteredNations] = useState([]);

    useEffect(() => {
        const timer = setTimeout(() => {
            setLetterClass("text-animate-hover");
        }, 3000); 

        return () => { 
            clearTimeout(timer);
        };
    }, []);  // Empty dependency array to ensure this runs only once

    // Filter nations based on search query
    useEffect(() => {
      const filtered = nationData.nations.filter(nation =>
          nation.name.toLowerCase().includes(searchQuery.toLowerCase())
      );
      setFilteredNations(filtered);
    }, [searchQuery]);

    // Handle search input change
    const handleSearchChange = event => {
        setSearchQuery(event.target.value);
    };

    // Render player images as nation flags and a "View" button
    const renderNationPlayers = (nations) => {
        return (
          <div className="images-container">
            {nations.map((nation, idx) => {
              // Find corresponding player by nation
              const player = playerData.players.find(p => p.nation === nation.name);

              return (
                <div key={idx} className="image-box">
                  {player ? (
                    <img 
                      src={players.uri} 
                      alt={player.name} 
                      className="player-image" 
                      style={{ width: '100%', height: '100%' }}
                    />
                  ) : (
                    <div>No player found</div>
                  )}

                  <div className="content">
                    <p className="title">{nation.name}</p>
                    {player && (
                      <Link className="btn" to={`/data?player=${encodeURIComponent(player.name)}`}>
                        View
                      </Link>
                    )}
                  </div>
                </div>
              );
            })}
          </div>
        );
    };

    return (
        <>
          <div className="container nation-page">
            <h1 className="page-title">
              <AnimatedLetters letterClass={letterClass} strArray={"Nations".split("")} idx={15} />
            </h1>
            <div className="search-bar">
                <input
                    type="text"
                    placeholder="Search for countries"
                    value={searchQuery}
                    onChange={handleSearchChange}
                />
            </div>
            <div>{renderNationPlayers(filteredNations)}</div>
          </div>
          <Loader type="pacman" />
        </>
    );
};

export default Nations;
