// src/pages/Home.js

import { useEffect, useState } from 'react';
import Loader from 'react-loaders';
import { Link } from 'react-router-dom';
import LogoNBA from '../../assets/images/NBA_logo.webp'; // Ensure you have the NBA logo in this path
import AnimatedLetters from '../AnimatedLetters';
import './index.scss';

const Home = () => {
    const [letterClass, setLetterClass] = useState('text-animate');
    const welcomeArray = "Welcome to".split("");
    const projectArray = "NBA Player Catalog!".split("");

    useEffect(() => {
        const timerId = setTimeout(() => {
            setLetterClass('text-animate-hover');
        }, 4000);

        return () => {
            clearTimeout(timerId);
        };
    }, []);

    return(
      <>
        <div className="container home-page">
            <div className="text-zone">
                <h1>
                    <img src={LogoNBA} alt="NBA Logo" className="nba-logo" />
                    <br />
                    <AnimatedLetters letterClass={letterClass} strArray={welcomeArray} idx={12} />
                    <br /> 
                    <AnimatedLetters letterClass={letterClass} strArray={projectArray} idx={15} /> 
                </h1>
                <h2>Your gateway to exploring NBA teams and players!</h2>
                <Link to="/teams" className="flat-button">GET STARTED</Link>
            </div>
        </div>
        <Loader type="pacman" />
      </>
    );
}

export default Home;
